var menu = getProduct();

localStorage.setItem("menu", JSON.stringify(menu));

for (var i = 0; i < menu.length; i++) {
    menuCreat(menu[i]);
}
if (localStorage.getItem("addToCart") == null) {
    arr = []
    localStorage.setItem("addToCart", JSON.stringify(arr))
}
else {
    arr = JSON.parse(localStorage.getItem("addToCart"))
}
function menuCreat(addmenu) {
    var contaier = document.getElementById('con')
    var div = document.createElement("div")
    var tittle = document.createElement("h3");
    var price = document.createElement("h3");
    var description = document.createElement("p");
    var image = document.createElement("img");
    var add = document.createElement("button");


    div.className = "div1"
    tittle.className = "title";
    price.className = "price";
    description.className = "desc";
    image.className = "img1";
    add.className = "button";


    add.innerText = "Add To Cart";
    add.onclick = function () {
        var btnorder = document.querySelectorAll('.button')[1];
        arr = JSON.parse(localStorage.getItem("addToCart"))
        arr.push(addmenu)
        localStorage.setItem("addToCart", JSON.stringify(arr))
        btnorder.innerHTML = 'My Order' + "(" + arr.length + ")";

    }

    tittle.innerText = addmenu.title;
    price.innerText = addmenu.price;
    description.innerText = addmenu.description;
    image.src = addmenu.images;



    div.append(image, tittle, price, description, add);
    contaier.style.display = 'flex';
    contaier.style.flexWrap = 'wrap';
    contaier.appendChild(div);
}

function searchMenu() {
    var search = document.getElementById('searchTerm').value;
    var contaier = document.getElementById('con')

    contaier.innerText = ' ';

    for (var i = 0; i < menu.length; i++) {
        if (menu[i].title.includes(search))
            menuCreat(menu[i]);
    }
}

/* filter function */

function getCategory(id) {
    var contaier = document.getElementById('con');
    contaier.innerText = ' ';
    if (id === 'breakfast') {
        for (var i = 0; i < menu.length; i++) {
            if (menu[i].category.name.includes("breakfast"))
                menuCreat(menu[i]);
        }
    } else if (id === 'lanch') {
        for (var i = 0; i < menu.length; i++) {
            if (menu[i].category.name.includes("lanuch"))
                menuCreat(menu[i]);
        }
    } else {
        for (var i = 0; i < menu.length; i++) {
            if (menu[i].category.name.includes("dinner"))
                menuCreat(menu[i]);
        }
    }
}




